mapper.update:
UPDATE GAMELIST
   SET G_SCORE =(
          SELECT AVG(R_SCORE)
            FROM BOARD
           WHERE G_IDX = #{g_idx}
             AND MENU_ID = 1
       )
 WHERE G_IDX = #{g_idx}

mapper.select:
SELECT COUNT(*)
  FROM GAMELIST
 WHERE G_NAME = ${de}

SELECT COUNT(*)
FROM GAMELIST
WHERE G_NAME = #{g}
